package ch02_Ex12;

public class CastingEx01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double d = 85.4;		//변수 double형 타입의 d 선언, 85.4저장
		int score = (int)d;		//변수 int형 타입의 score 선언, (int)d저장
		
		System.out.println("score=" + score);	//score 출력
		System.out.println("d=" + d);			//d 출력
		
	}

}
